import { Component, OnInit } from '@angular/core';
import { User } from '../shared/users.model';
import { UserService } from '../shared/users.service';
import { Observer } from 'rxjs';

@Component({
  selector: 'app-user',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css'],
})
export class UserComponent implements OnInit {
  users: User[] = [];
  selectedUser: User | null = null;
  isEditMode = false;

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.getUsers();
  }

  getUsers() {
    this.userService.getUsers().subscribe({
      next: (users) => {
        this.users = users;
      },
      error: (error) => {
        console.error('Error fetching users:', error);
      },
    });
  }

  editUser(user: User) {
    this.isEditMode = true;
    this.selectedUser = { ...user };
  }

  selectUser(user: User) {
    this.selectedUser = { ...user };
    this.isEditMode = true;
  }

  createUser(user: User) {
    this.userService.createUser(user).subscribe({
      next: (newUser) => {
        this.users.push(newUser);
        this.clearSelectedUser();
      },
      error: (error) => {
        console.error('Error creating user:', error);
      },
    });
  }

  updateUser(user: User) {
    this.userService.updateUser(user).subscribe({
      next: (updatedUser) => {
        const index = this.users.findIndex((u) => u._id === updatedUser._id);
        if (index !== -1) {
          this.users[index] = updatedUser;
          this.clearSelectedUser();
        }
      },
      error: (error) => {
        console.error('Error updating user:', error);
      },
    });
  }

  deleteUser(userId: string) {
    this.userService.deleteUser(userId).subscribe({
      next: () => {
        this.users = this.users.filter((u) => u._id !== userId);
        this.clearSelectedUser();
      },
      error: (error) => {
        console.error('Error deleting user:', error);
      },
    });
  }

  clearSelectedUser() {
    this.selectedUser = null;
    this.isEditMode = false;
  }
}
<h1>Users</h1>
<ul>
  <li *ngFor="let user of users">
    {{ user.first_name }} {{ user.last_name }} - {{ user.email }}
    <button (click)="editUser(user)">Edit</button>
    <button (click)="deleteUser(user._id)">Delete</button>
  </li>
</ul>

<hr>

<h2>Create User</h2>
<form (submit)="createUser(newUserForm)">
  <input type="text" name="new_first_name" placeholder="First Name" [(ngModel)]="newUserForm.first_name" required>
  <input type="text" name="new_last_name" placeholder="Last Name" [(ngModel)]="newUserForm.last_name" required>
  <input type="email" name="new_email" placeholder="Email" [(ngModel)]="newUserForm.email" required>
  <button type="submit">Create</button>
</form>

<hr>

<h2>Edit User</h2>
<form *ngIf="selectedUser" (submit)="updateUser(createUser)">
  <input type="text" name="edit_first_name" placeholder="First Name" [(ngModel)]="createUser.first_name" required>
  <input type="text" name="edit_last_name" placeholder="Last Name" [(ngModel)]="createUser.last_name" required>
  <input type="email" name="edit_email" placeholder="Email" [(ngModel)]="createUser.email" required>
  <button type="submit">Update</button>
</form>
