import { Component, OnInit } from '@angular/core';
import { Users } from '../shared/users.model';
import { NgForm } from '@angular/forms';

import { UsersService } from '../shared/users.service';
declare var M: any;

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css'],
  providers: [UsersService],
})
export class UsersComponent implements OnInit {
  constructor(public userService: UsersService) {}
  ngOnInit(): void {
    this.resetForm();
    this.refreshUsersList();
  }
  resetForm(form?: NgForm) {
    if (form) form.reset();
    this.userService.selectedUsers = {
      _id: '',
      first_name: '',
      last_name: '',
      email: '',
      password: '',
    };
  }

  onSubmit(form: NgForm) {
    if (form.value._id == '') {
      this.userService.postUsers(form.value).subscribe((res) => {
        this.resetForm(form);
        this.refreshUsersList();
        M.toast({ html: 'Saved successfully', classes: 'rounded' });
      });
    } else {
      this.userService.putUsers(form.value).subscribe((res) => {
        this.resetForm(form);
        this.refreshUsersList();
        M.toast({ html: 'Updated successfully', classes: 'rounded' });
      });
    }
  }

  refreshUsersList() {
    this.userService.getUsersList().subscribe((res) => {
      this.userService.users = res as Users[];
    });
  }

  onEdit(emp: Users) {
    this.userService.selectedUsers = emp;
  }

  onDelete(_id: string, form: NgForm) {
    if (confirm('Are you sure to delete this record ?') == true) {
      this.userService.deleteUsers(_id).subscribe((res) => {
        this.refreshUsersList();
        this.resetForm(form);
        M.toast({ html: 'Deleted successfully', classes: 'rounded' });
      });
    }
  }
}
<div class="row">
    <div class="col s12">
      <div class="card">
        <div class="card-content white-text">
          <div class="row">
            <div class="col s5">
              <form #UsersForm="ngForm" (ngSubmit)="onSubmit(UsersForm)">
                <input type="hidden" name="_id" #_id="ngModel" [(ngModel)]="userService.selectedUsers._id">
                <div class="row">
                  <div class="input-field col s12">
                    <input type="text" name="first_name" #name="ngModel" [(ngModel)]="userService.selectedUsers.first_name" placeholder="Enter full name" required>
                    <label>First Name :
                      <label class="red-text">*</label>
                    </label>
                  </div>
                </div>
                <div class="row">
                  <div class="input-field col s12">
                    <input type="text" name="last_name" #name="ngModel" [(ngModel)]="userService.selectedUsers.last_name" placeholder="Eg : Snr. Developer">
                    <label>last name :</label>
                  </div>
                </div>
                <div class="row">
                  <div class="input-field col s12">
                    <input type="text" name="email" #name="ngModel" [(ngModel)]="userService.selectedUsers.email" placeholder="Enter office location">
                    <label>Office :</label>
                  </div>
                </div>
                <div class="row">
                  <div class="input-field col s12">
                    <input type="password" name="password" #name="ngModel" [(ngModel)]="userService.selectedUsers.password" placeholder="Enter Password">
                    <label>Enter Password</label>
                  </div>
                </div>
                <div class="row">
                  <div class="input-field col s12">
                    <button class="btn btn-custom right" type="button" (click)="resetForm(UsersForm)">Reset</button>
                    <button class="btn btn-custom right" type="submit" [disabled]="!UsersForm.valid">Submit</button>
                  </div>
                </div>
              </form>
            </div>
            <div class="col s7">
                <table class="responsive-table highlight">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Office</th>
                        <th></th>
                      </tr>
                    </thead>
                    <tr *ngFor="let emp of usersService.users">
                      <td>{{emp.name}}</td>
                      <td>{{emp.position}}</td>
                      <td>{{emp.office}}</td>
                      <td>
                          <a class="action-btn" (click)="onEdit(emp)">
                            <i class="material-icons">edit</i>
                          </a>
                          <a class="action-btn" (click)="onDelete(emp._id,UsersForm)">
                            <i class="material-icons">delete</i>
                          </a>
                        </td>
                    </tr>
                  </table>
            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
